package thema3verzweigung;

public class IfElse {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		int zahl =10;
		if(zahl >10) {
			System.out.println("Zahl ist größer als 10.");
		} else if (zahl ==10){
			System.out.println("Die Zahl ist gleich 10");
			
		}else {
			System.out.println("Zahl ist kleine als 10.");

	}

}}
